/* eslint-diable*/

import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {

  let post =  '강남 우동 맛집';
  let [글제목,글제목변경] = useState(['남자 코트 추천','강남 우동 맛집','파이썬독학']);
  let[따봉,따봉변경] = useState(0);

  // function 함수(){
  //   console.log(1);
  // }

  // let[logo, setLogo] = useState('ReactBlog');

  //a : state에 보관했던 자료 나옴
  //b : state변경 도와주는 함수


  return (
    <div className="App">
      <div className="black-nav">
        <h4>ReactBlog</h4>
      </div>

      <button>가나다순정렬</button>

      <button onClick={()=>{
        let copy = [...글제목];
        //...괄호 벗겨주세요 []괄호를 다시 씌어주세요=> 화살표도 달라짐
        copy[0] = '여자 코트 추천';
        글제목변경(copy);//새로운 state
      }}>글수정</button>

      <div className="list">
        <h4>{글제목[0]}<span onClick={()=>{따봉변경(따봉+1)}}>❤</span>{따봉}</h4>
        <p>2월 17일 발행</p>
      </div>
      <div className="list">
        <h4>{글제목[1]}</h4>
        <p>2월 17일 발행</p>
      </div>
      <div className="list">
        <h4>{글제목[2]}</h4>
        <p>2월 17일 발행</p>
      </div>
      <Modal></Modal>


    </div>
  );
}

function Modal(){
  return (
    <>
    <div className="modal">
      <h4>제목</h4>
      <p>날짜</p>
      <p>상세내용</p>
    </div>
    <div>

    </div>
    </>
  )
}

export default App;
